package org.example;

public class App {
    public static void main(String[] args) {
        RentalAgency agency = new RentalAgency();

        // Add vehicles
        agency.addVehicle(new Car("C1", "Toyota Corolla", 50.0, true));
        agency.addVehicle(new Car("C2", "Mercedes Maybach", 45.0, false));
        agency.addVehicle(new Motocycle("m1", "Ducatti", 44));
        agency.addVehicle(new Truck("t1", "Ford", 94));

        // View vehicles
        agency.viewVehicles();

        // Rent a vehicle
        Customer customer1 = new Customer("Kofi", 25);
        Customer customer2 = new Customer("Ama" , 83);
        Customer customer3 = new Customer("Yaa" , 32);

        agency.rentVehicle("C1", customer1, 5);
        agency.rentVehicle("m1", customer2, 2);
        agency.rentVehicle("t1", customer3, 3);



        // View transactions
        agency.viewTransactions();

        // Return a vehicle
        agency.returnRentedVehicle("T1");

        // View transactions again
        agency.viewTransactions();
    }
}
